/*Se crea la base de datos */
drop schema if exists practica;
drop user if exists usuario_practica;
CREATE SCHEMA practica ;

/*Se crea un usuario para la base de datos llamado "usuario_practica" y tiene la contrase√±a "la_Clave."*/
create user 'usuario_practica'@'%' identified by 'la_Clave.';

/*Se asignan los prvilegios sobr ela base de datos TechShop al usuario creado */
grant all privileges on practica.* to 'usuario_practica'@'%';
flush privileges;

CREATE TABLE practica.arbol (
    id INT NOT NULL AUTO_INCREMENT,
    ruta_imagen VARCHAR(1024),
    activo bool,
    nombre_comun TEXT,
    tipo_flor TEXT,
    dureza_madera int,
    PRIMARY KEY (id))
    ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8mb4;
 
 CREATE TABLE practica.usuario (
  id_usuario INT NOT NULL AUTO_INCREMENT,
  username varchar(20) NOT NULL,
  password varchar(512) NOT NULL,
  nombre VARCHAR(20) NOT NULL,
  apellidos VARCHAR(30) NOT NULL,
  correo VARCHAR(25) NULL,
  telefono VARCHAR(15) NULL,
  ruta_imagen varchar(2048), 
  categoriafactura varchar(50), 
  activo boolean,
  PRIMARY KEY (`id_usuario`)
) ENGINE = InnoDB DEFAULT CHARACTER SET = utf8mb4;


create table practica.factura (
  id_factura INT NOT NULL AUTO_INCREMENT,
  id_usuario INT NOT NULL,
  fecha date,  
  total double,
  estado int,
  PRIMARY KEY (id_factura),
  foreign key fk_factura_usuario (id_usuario) references usuario(id_usuario)  
)
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8mb4;

create table practica.producto (
  id_producto INT NOT NULL AUTO_INCREMENT,
  id INT NOT NULL,
  descripcion VARCHAR(30) NOT NULL,  
  detalle VARCHAR(1600) NOT NULL, 
  precio double,
  existencias int,  
  ruta_imagen varchar(2048),
  activo bool,
  PRIMARY KEY (id_producto),
  foreign key fk_producto_arbol (id) references arbol(id)  
)
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8mb4;



